mapper.select:
SELECT
	COUNT(*)
FROM
	insa

	
		AND sabun = #{sabun}
	
	
		AND name = LIKE '%' || #{name} ||'%'
	
	
		AND join_yn = #{join_yn}
	
	
		AND put_yn = #{put_yn}
	
	
		AND pos_gbn_code = #{pos_gbn_code}
		AND position.code = pos_gbn_code
	
	
		AND join_day = #{join_day_sql}
	
	
		AND retire_day = #{retire_day_sql}
	
	
		AND join_gbn_code = #{join_gbn_code}
		AND job.code = join_gbn_code

SELECT ROWNUM rno, R.* FROM (SELECT sabun, insa.name, reg_no, hp, pos, join_day, retire_day, put_yn, salary FROM insa, (SELECT code, com.name pos FROM insa, insa_com com WHERE gubun = '직위' ) position, (SELECT code FROM insa, insa_com com WHERE gubun = '직종' ) job
AND sabun = #{sabun}
AND name = LIKE '%' || #{name} ||'%'
AND join_yn = #{join_yn}
AND put_yn = #{put_yn}
AND pos_gbn_code = #{pos_gbn_code} AND position.code = pos_gbn_code
AND join_day = #{join_day_sql}
AND retire_day = #{retire_day_sql}
AND join_gbn_code = #{join_gbn_code} AND job.code = join_gbn_code
ORDER BY sabun) R WHERE rno BETWEEN #{startCont} AND #{endCont}

mapper.insert:
INSERT INTO input_info_del
SELECT * FROM insa_input_info

	
		AND sabun = #{sabun}
	
	
		AND seq = #{iseq}

INSERT INTO acad_del
SELECT * FROM insa_acad_ability

	
		AND sabun = #{sabun}
	
	
		AND seq = #{aseq}

INSERT INTO carrier_del
SELECT * FROM insa_carrier

	
		AND sabun = #{sabun}
	
	
		AND seq = #{cseq}

mapper.delete:
DELETE FROM
	insa_input_info

	
		AND sabun = #{sabun}
	
	
		AND seq = #{iseq}

DELETE FROM
	insa_carrier

	
		AND sabun = #{sabun}
	
	
		AND seq = #{cseq}

DELETE FROM
	insa_acad_ability

	
		AND sabun = #{sabun}
	
	
		AND seq = #{aseq}
